const express = require('express');
const axios = require('axios');

const app = express();
const PORT = process.env.PORT || 5000;

app.use(express.json());

// Define endpoint /v1/users
app.get('/v1/users', async (req, res) => {
  try {
    const searchText = req.query.searchText;
    const usersResponse = await axios.get('https://jsonplaceholder.typicode.com/users');
    const postsResponse = await axios.get('https://jsonplaceholder.typicode.com/posts');

    // Combine user and post data based on userId
    const combinedData = usersResponse.data.map(user => ({
      ...user,
      posts: postsResponse.data.filter(post => post.userId === user.id),
    }));

    // If searchText provided, filter results by user name
    if (searchText) {
      const filteredData = combinedData.filter(user =>
        user.name.toLowerCase().includes(searchText.toLowerCase())
      );
      return res.json(filteredData);
    }

    res.json(combinedData);
  } catch (error) {
    console.error(error);
    res.status(500).json({ error: 'Internal Server Error' });
  }
});

app.listen(PORT, () => {
  console.log(`Server is running on http://localhost:${PORT}`);
});
